  }
`;

interface ProjectDetailsProps {
  project: Project;
  onClose: () => void;
}

export const ProjectDetails: React.FC<ProjectDetailsProps> = ({ project, onClose }) => {
  return (
    <DetailsContainer>
      <Title>{project.name}</Title>
      
      <Section>
        <SectionTitle>Overview</SectionTitle>
        <Description>{project.overview}</Description>
      </Section>

      <Section>
